        static void PrintMaze(char[,] maze)
        {
            for (int i = 0; i < maze.GetLength(0); i++)
            {
                for (int j = 0; j < maze.GetLength(1); j++)
                {
                    Console.Write(maze[i, j]);
                }
                Console.WriteLine();
            }
        }

        static void HandlePlayerMovement(char[,] maze, Player player)
        {
            if (Keyboard.IsKeyPressed(Key.LeftArrow))
            {
                MovePlayer(maze, player, 0, -1);
            }
            else if (Keyboard.IsKeyPressed(Key.RightArrow))
            {
                MovePlayer(maze, player, 0, 1);
            }
            else if (Keyboard.IsKeyPressed(Key.UpArrow))
            {
                MovePlayer(maze, player, -1, 0);
            }
            else if (Keyboard.IsKeyPressed(Key.DownArrow))
            {
                MovePlayer(maze, player, 1, 0);
            }
        }

        static void MovePlayer(char[,] maze, Player player, int deltaX, int deltaY)
        {
            int newX = player.X + deltaX;
            int newY = player.Y + deltaY;

            if (IsMoveValid(maze, newX, newY))
            {
                player.Erase();
                player.Move(newX, newY);
                player.Print();
            }
        }

        static void MoveEnemies(Enemy enemy, ref string direction)
        {
            enemy.Erase();
            direction = UpdateDirection(enemy.Y, ref direction);
            enemy.Move(direction);
            enemy.Print();
        }

        static string UpdateDirection(int y, ref string direction)
        {
            if (y == 29)
            {
                direction = "Up";
            }
            else if (y == 1)
            {
                direction = "Down";
            }
            return direction;
        }

        static bool IsMoveValid(char[,] maze, int x, int y)
        {
            return x >= 0 && x < maze.GetLength(0) && y >= 0 && y < maze.GetLength(1) && maze[x, y] == ' ';
        }
    }
